from typing import List
import time

class Solution:
    # count = 0
    def shortestBridge(self, grid: List[List[int]]) -> int:
        # start_time = time.time()
        n = len(grid)
        self.color_island(grid, n)
        # print(time.time() - start_time)
        # start_time = time.time()
        i = 0
        island_1 = []
        island_2 = []
        directions = [(0, -1), (-1, 0), (0, 1), (1, 0)]
        while i < n:
            j = 0
            while j < n:
                if grid[i][j] == 1:
                    flag = False
                    for step_x, step_y in directions:
                        next_x = i + step_x
                        next_y = j + step_y
                        if 0 <= next_x < n and 0 <= next_y < n:
                            if grid[next_x][next_y] != grid[i][j]:
                                flag = True
                                break
                    if flag:
                        island_1.append((i, j))
                elif grid[i][j] == -1:
                    flag = False
                    for step_x, step_y in directions:
                        next_x = i + step_x
                        next_y = j + step_y
                        if 0 <= next_x < n and 0 <= next_y < n:
                            if grid[next_x][next_y] != grid[i][j]:
                                flag = True
                                break
                    if flag:
                        island_2.append((i, j))
                j += 1
            i += 1
        # print(time.time() - start_time)
        # start_time = time.time()
        res = 10000
        for x1, y1 in island_1:
            for x2, y2 in island_2:
                res = min(res, abs(x1 - x2) + abs(y1 - y2) - 1)
        # print(time.time() - start_time)
        return res

    def color_island(self, grid, n):
        def color(grid, n, x, y):
            q = [(x, y)]
            directions = [(0, -1), (-1, 0), (0, 1), (1, 0)]
            while len(q) > 0:
                x, y = q[0]
                for step_x, step_y in directions:
                    next_x = x + step_x
                    next_y = y + step_y
                    if 0 <= next_x < n and 0 <= next_y < n:
                        if grid[next_x][next_y] == 1:
                            q.append((next_x, next_y))
                            grid[next_x][next_y] = -1
                q = q[1:]
                
        i = 0
        while i < n:
            j = 0
            while j < n:
                if grid[i][j] == 1:
                    grid[i][j] = -1
                    color(grid, n, i, j)
                    return
                j += 1
            i += 1
        


